package com.example.androideventdraw;

public class Dot {
	
	private float x, y;
	private boolean b;

	public Dot(float x, float y, boolean b) {
		// TODO Auto-generated constructor stub
		this.x = x;
		this.y = y;
		this.b = b;
	}

	public float getY() {
		return y;
	}

	public void setY(float y) {
		this.y = y;
	}

	public boolean isB() {
		return b;
	}

	public void setB(boolean b) {
		this.b = b;
	}

	public float getX() {
		return x;
	}

	public void setX(float x) {
		this.x = x;
	}

}
